import tkinter as tk
from tkinter import filedialog
from tkinter import messagebox
from tkinter import ttk
import os
from pandas import read_csv
from pyexcelerate import Workbook
from threading import Thread
import tkinter as GUI

global status
status = False

def OpenCSV():
    my_filetypes = [ ('csv files', '.csv')]
    paths = filedialog.askopenfilenames(parent=application_window,initialdir=os.getcwd(),title="Please select one or more files:",filetypes=my_filetypes)
    comma= ','
    path_bar_Entry.delete(0,"end")
    path_bar_Entry.insert(0,comma.join(paths))

def ConvertToXLSX():
    convert = ttk.Button(application_window,text = "Convert to XLSX" ,state=GUI.DISABLED )
    convert.grid(column = 20, row = 40,padx=10,pady=10)

    def Convert(ans):
        global status
        csv_file = ans
        excel_file=csv_file.replace('.csv','.xlsx')
        try:
            df = read_csv(csv_file, sep=',', error_bad_lines=False, index_col=False, dtype='unicode')
            df.head()
            data = [df.columns.tolist(), ] + df.values.tolist()
            wb = Workbook()
            wb.new_sheet('Sheet 1', data=data)
            wb.save(excel_file)
            status= False

        except:
            messagebox.showinfo(message= "Please Select CSV Correctly" )
            status= True

    def Loop(x, thread01):
        while x == True:
            thread01.join()
            convert = ttk.Button(application_window,text = "Convert to XLSX" ,command=ConvertToXLSX)
            convert.grid(column = 20, row = 40,padx=10,pady=10)
            if status ==False:
                messagebox.showinfo(message= "File Converted to XLSX" )

            x = thread01.is_alive()
    paths=path_bar.get()
    paths=paths.split(',')

    for path in paths:
        thread01   = Thread(target = Convert , args = (  path ,))
        thread01.start()
        print('file '+path.split('/')[-1]+' start')
    thread02   = Thread(target = Loop , args = (  thread01.is_alive() , thread01,))
    thread02.start()   


def Exit():
    message_status=messagebox.askokcancel("Exit", "Do you want to Exit?")
    if message_status == True:
        application_window.destroy()




icon = ''' '''


application_window = tk.Tk()
img = tk.PhotoImage(data=icon)
application_window.tk.call('wm', 'iconphoto', application_window._w, img)
application_window.title("Bulk CSV to XLSX Converter")
application_window.resizable(False, False)



path_bar = tk.StringVar()
path_bar_Entry = ttk.Entry(application_window,width=12,textvariable=path_bar )
path_bar_Entry.grid(column=0,row = 12 , columnspan=200,rowspan=10, sticky="we",padx=10,pady=10)


action = ttk.Button(application_window,text = "Add CSV Path" ,command=OpenCSV)
action.grid(column = 15, row = 40,padx=10)

convert = ttk.Button(application_window,text = "Convert to XLSX" ,command=ConvertToXLSX)
convert.grid(column = 20, row = 40,padx=10,pady=5)


abort = ttk.Button(application_window,text = "Exit" ,command=Exit)
abort.grid(column = 25, row = 40,padx=10,pady=10)


application_window.protocol("WM_DELETE_WINDOW", Exit)
application_window.mainloop()













